"use strict";

module.exports = function (objA, objB) {
    if (objA === objB) {
        return true;
    }

    var keysA = Object.keys(objA);
    var keysB = Object.keys(objB);
    var len = keysA.length;

    if (keysB.length !== len) {
        return false;
    }

    for (var i = 0; i < len; i++) {
        var key = keysA[i];
        if (objA[key] !== objB[key]) {
            return false;
        }
    }

    return true;
};